import React, { useState ,useEffect ,useRef} from 'react';
import './adminshow.css';
import axios from 'axios';
import Swal from 'sweetalert2';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import { Client } from '@microsoft/microsoft-graph-client';
function OpenCloseComponent() {
  const [isOpen, setIsOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');
  const [excelData, setExcelData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const client = Client.init({
          authProvider: (done) => {
            done(null, accessToken); // แทน accessToken ด้วย token การอนุญาตที่คุณได้รับจากการยินยอมใน Azure Active Directory
          }
        });

        const response = await client
          .api('/me/drive/root:/path/to/your/excel/file.xlsx:/workbook/worksheets(\'Sheet1\')/tables(\'Table1\')/rows')
          .get();

        setExcelData(response.value); // response.value เป็นข้อมูลที่คุณดึงมาจาก Excel
      } catch (error) {
        console.error('Error fetching Excel data:', error);
      }
    };

    fetchData();
  }, []);

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
    setIsAuthenticated(false); // เมื่อปิดโมดัลเลอร์ให้รีเซ็ตสถานะการตรวจสอบสิทธิ์การเข้าสู่ระบบ
  };

  const handleConfirm = () => {
    // ตรวจสอบว่าชื่อผู้ใช้และรหัสผ่านถูกต้องหรือไม่
    if (user === 'admin' && password === 'password') {
      setIsAuthenticated(true);
      setIsOpen(false); // ปิดโมดัลเลอร์หลังจากเข้าสู่ระบบสำเร็จ
    } else {
      Swal.fire({
        icon: 'error',
        title: 'เข้าสู่ระบบล้มเหลว',
        text: 'ชื่อผู้ใช้หรือรหัสผ่านไม่ถูกต้อง'
      });
    }
  };

  const handleExportToPDF = () => {
    // ฟังก์ชันสำหรับสร้าง PDF
  };

  return (
    <div class="show-content">
        
      {!isOpen && (
        <button onClick={handleOpen} class="buttonshow">
        <div class="svg-wrapper-1">
          <div class="svg-wrapper">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              width="30"
              height="30"
              class="icon"
            >
              <path
                d="M22,15.04C22,17.23 20.24,19 18.07,19H5.93C3.76,19 2,17.23 2,15.04C2,13.07 3.43,11.44 5.31,11.14C5.28,11 5.27,10.86 5.27,10.71C5.27,9.33 6.38,8.2 7.76,8.2C8.37,8.2 8.94,8.43 9.37,8.8C10.14,7.05 11.13,5.44 13.91,5.44C17.28,5.44 18.87,8.06 18.87,10.83C18.87,10.94 18.87,11.06 18.86,11.17C20.65,11.54 22,13.13 22,15.04Z"
              ></path>
            </svg>
          </div>
        </div>
        <span>Admin</span>
      </button>
      
      )}
      {isOpen && !isAuthenticated && (
        
        <div class="show-co">
            <div class="retop">
                <h2>LOGIN</h2>
            </div>
            <div class="input-container">
            <input placeholder="Username" class="input-field" type="text"value={user}
              onChange={(e) => setUser(e.target.value)}></input>
            <label for="input-field" class="input-label">Username</label>
            <span class="input-highlight"></span>
            </div>
          <br />
          <div class="input-container">
            <input placeholder="Password" class="input-field" type="password"value={password}
              onChange={(e) => setPassword(e.target.value)}></input>
            <label for="input-field" class="input-label">Password</label>
            <span class="input-highlight"></span>
            </div>
          <br />
          <button class="button"onClick={handleConfirm}> ยืนยัน
            </button>
          <button class="btns"onClick={handleClose}>กลับ</button>
        </div>
      )}
      {isAuthenticated && (
        <div class="show-co">
          <div class="table-wrapper" ref={componentRef}>
          <h2>รายชื่อผู้สมัครอนุสโมสรคณะศิลปศาสตร์</h2><br></br>

          <table>
        <thead>
          <tr>
            <th>Column 1</th>
            <th>Column 2</th>
            {/* เพิ่มหัวคอลัมน์อื่น ๆ ตามต้องการ */}
          </tr>
        </thead>
        <tbody>
          {excelData.map((row, index) => (
            <tr key={index}>
              <td>{row.column1}</td>
              <td>{row.column2}</td>
              {/* เพิ่มเซลล์ข้อมูลอื่น ๆ ตามต้องการ */}
            </tr>
          ))}
        </tbody>
      </table>
          
        </div>
        <button class="closebtn"onClick={handleExportToPDF}>บันทึกเป็น PDF</button>
          <button class="closebtn" onClick={handleClose}>ปิด</button>
      </div>
      )}
      
    </div>
  );
}

export default OpenCloseComponent;
