const mongoose = require('mongoose');
const url=('mongodb+srv://fullcharlotte:<password>@regisdata.ae0q7td.mongodb.net/');
// กำหนดโครงสร้างข้อมูลสำหรับตาราง "stdregis"
const stdRegisSchema = new mongoose.Schema({
  // กำหนดฟิลด์ข้อมูลตามที่คุณต้องการ
  // ตัวอย่างเช่น:
  studentId: String,
  studentName: String,
  major: String,
  // และฟิลด์อื่นๆ ตามความต้องการ
});

// สร้างโมเดล "StdRegis" จากโครงสร้างข้อมูล
const StdRegis = mongoose.model('StdRegis', stdRegisSchema);

// เชื่อมต่อ MongoDB Atlas
mongoose.connect(url, {
  useNewUrlParser: true,
  useUnifiedTopology: true
});

const db = mongoose.connection;
db.on('error', console.error.bind(console, 'connection error:'));
db.once('open', () => {
  console.log('Connected to the database.');
});

module.exports = StdRegis;
