import React, { useState, useEffect } from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

function OpenCloseComponent() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('https://sheet.best/api/sheets/197a5f20-fa48-43f8-8faf-2837bf44b6e6');
      const jsonData = await response.json();
      setData(jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleExportToPDF = () => {
    const table = document.getElementById('.abs');

    html2canvas(table, { scrollY: -window.scrollY }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF();
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('table.pdf');
    });
  };

  return (
    <div>
      <button onClick={handleExportToPDF}>บันทึกเป็น PDF</button>
      <div class="abs">
      <table id="data-table">
        <thead>
          <tr>
            <th>Fullname</th>
            <th>Std ID</th>
            <th>Level</th>
            <th>Subject</th>
            <th>About</th>
          </tr>
        </thead>
        <tbody>
            <tr key={index}>
              <td>1212</td>
              <td>3242</td>
              <td>67867</td>
              <td>fghfh</td>
              <td>54645</td>
            </tr>
        </tbody>
      </table>
      </div>
    </div>
  );
}

export default OpenCloseComponent;
