import React, { useState } from 'react';

function OpenCloseComponent() {
  const [isOpen, setIsOpen] = useState(false);
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');

  const handleOpen = () => {
    if (user === 'Admin' && password === '111') {
      setIsOpen(true);
    } else {
      alert('ผู้ใช้หรือรหัสผ่านไม่ถูกต้อง');
    }
  };

  const handleClose = () => {
    setIsOpen(false);
    setUser('');
    setPassword('');
  };

  return (
    <div>
      <label>
        ชื่อผู้ใช้:
        <input
          type="text"
          value={user}
          onChange={(e) => setUser(e.target.value)}
        />
      </label>
      <br />
      <label>
        รหัสผ่าน:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </label>
      <br />
      <button onClick={handleOpen}>เปิด</button>
      {isOpen && (
        <div>
          <p>ข้อความที่ต้องการแสดงเมื่อกดเปิด</p>
          <button onClick={handleClose}>ปิด</button>
        </div>
      )}
    </div>
  );
}

export default OpenCloseComponent;
