import { useState } from "react";
import Axios from "axios";
import { Form } from "react-bootstrap";
import { Button } from "react-bootstrap";

function App() {
  const [fullname, setFullname] = useState("");
  const [stdid, setStdid] = useState(0);
  const [level, setLevel] = useState("");
  const [subj, setsubj] = useState("");
  const [about, setAbout] = useState(0);

  const [employeeList, setEmployeeList] = useState([]);

  const getEmployees = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      setEmployeeList(response.data);
    });
  };

  const addEmployee = () => {
    Axios.post("http://localhost:3001/create", {
      fullname: fullname,
      stdid: stdid,
      level: level,
      subj: subj,
      about: about,
    }).then(() => {
      setEmployeeList([
        ...employeeList,
        {
        fullname: fullname,
        stdid: stdid,
        level: level,
        subj: subj,
        about: about,
        },
      ]);
    });
  };


  const deleteEmployee = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setEmployeeList(
        employeeList.filter((val) => {
          return val.id != id;
        })
      );
    });
  };

  {employeeList.map((val, key) => {
    return (
      <div className="employee card" key={key}>
        <div className="card-body text-left">
          <p className="card-text">Name: {val.fullname}</p>
          <p className="card-text">Age: {val.stdid}</p>
          <p className="card-text">Country: {val.level}</p>
          <p className="card-text">Position: {val.subj}</p>
          <p className="card-text">Wage: {val.about}</p>
          <div className="d-flex">
            <input
              className="form-control"
              style={{ width: "300px" }}
              type="number"
              placeholder="15000..."
              onChange={(event) => {
                setNewWage(event.target.value)
              }}
            />
            <button className="btn btn-warning" onClick={() => {updateEmployeeWage(val.id)}}>Update</button>
            <button className="btn btn-danger" onClick={() => {deleteEmployee(val.id)}}>Delete</button>
          </div>
        </div>
      </div>
    );
  })}
}
export default App;
