import React, { useState, useEffect } from 'react';

const TextAnimation = () => {
  const [text, setText] = useState('');
  const originalText = "ข้อความที่คุณต้องการให้แสดงเป็นการพิมพ์ดีด";
  let count = 0;

  useEffect(() => {
    const type = () => {
      if (count < originalText.length) {
        setText(prevText => prevText + originalText.charAt(count));
        count++;
        setTimeout(type, Math.random() * 200 + 50);
      }
    };

    type();
  }, []); // คำนึงถึงการ render ครั้งแรกเท่านั้น

  return (
    <p>{text}</p>
  );
};

export default TextAnimation;
