import React, { useState } from 'react';
import './adminshow.css';
import Swal from 'sweetalert2';
function OpenCloseComponent() {
  const [isOpen, setIsOpen] = useState(false);
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
    setUser('');
    setPassword('');
    setIsAuthenticated(false);
  };

  const handleConfirm = () => {
    if (user === 'Admin' && password === '111') {
      setIsAuthenticated(true);
    } else {
        Swal.fire({
            title: 'เอ๊ะ',
            text: 'Admin ตัวปล่อม?',
            icon: 'error',
            confirmButtonText: 'Ok',
          })
    }
  };

  return (
    <div class="show-content">
      {!isOpen && (
        <button onClick={handleOpen} class="buttonshow">
        <div class="svg-wrapper-1">
          <div class="svg-wrapper">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              width="30"
              height="30"
              class="icon"
            >
              <path
                d="M22,15.04C22,17.23 20.24,19 18.07,19H5.93C3.76,19 2,17.23 2,15.04C2,13.07 3.43,11.44 5.31,11.14C5.28,11 5.27,10.86 5.27,10.71C5.27,9.33 6.38,8.2 7.76,8.2C8.37,8.2 8.94,8.43 9.37,8.8C10.14,7.05 11.13,5.44 13.91,5.44C17.28,5.44 18.87,8.06 18.87,10.83C18.87,10.94 18.87,11.06 18.86,11.17C20.65,11.54 22,13.13 22,15.04Z"
              ></path>
            </svg>
          </div>
        </div>
        <span>Admin</span>
      </button>
      
      )}
      {isOpen && !isAuthenticated && (
        
        <div class="show-co">
            <div class="retop">
                <h2>LOGIN</h2>
            </div>
            <div class="input-container">
            <input placeholder="Username" class="input-field" type="text"value={user}
              onChange={(e) => setUser(e.target.value)}></input>
            <label for="input-field" class="input-label">Username</label>
            <span class="input-highlight"></span>
            </div>
          <br />
          <div class="input-container">
            <input placeholder="Pass" class="input-field" type="text"value={password}
              onChange={(e) => setPassword(e.target.value)}></input>
            <label for="input-field" class="input-label">Password</label>
            <span class="input-highlight"></span>
            </div>
          <br />
          <button class="button"onClick={handleConfirm}> ยืนยัน
            </button>
          <button class="btns"onClick={handleClose}>ปิด</button>
        </div>
      )}
      {isAuthenticated && (
        <div class="show-co">
          <p>ข้อความที่ต้องการแสดงเมื่อกดเปิด</p>
          <button onClick={handleClose}>ปิด</button>
        </div>
      )}
    </div>
  );
}

export default OpenCloseComponent;
