import React, { useState, useEffect } from "react";
import './MyButton.css';

function MyButton() {
  // ใช้ useState hooks เพื่อเก็บค่าของข้อมูล
  const [formData, setFormData] = useState({
    fullName: "",
    stdid: "",
    level: "",
    subj: "",
    about: ""
  });

  // ฟังก์ชันเมื่อมีการเปลี่ยนแปลงค่าในฟอร์ม
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  // ฟังก์ชันสำหรับการส่งข้อมูลเมื่อกดปุ่มสมัคร
  const handleSubmit = () => {
    // ทำอะไรกับข้อมูลที่สร้างขึ้นใน formData
    console.log(formData);
  };

  return (
    <div className="index-content">
      <div id="container">
      </div>
      <div className="labelainput">
        <form>
          <div>
            <label htmlFor="fullname">ชื่อ-สกุล:</label>
            <input type="text" id="fullName" name="fullName" value={formData.fullName} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="student_id">รหัสนักศึกษา:</label>
            <input type="number" id="stdid" name="stdid" value={formData.stdid} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="year">ชั้นปี:</label>
            <select id="level" name="level" value={formData.level} onChange={handleChange}>
              <option value="ชั้นปีที่ 1">ชั้นปีที่ 1</option>
              <option value="ชั้นปีที่ 2">ชั้นปีที่ 2</option>
              <option value="ชั้นปีที่ 3">ชั้นปีที่ 3</option>
              <option value="ชั้นปีที่ 4">ชั้นปีที่ 4</option>
            </select>
          </div>
          <div>
            <label htmlFor="department">หลักสูตร:</label>
            <select id="subj" name="subj" value={formData.subj} onChange={handleChange}>
              {/* ตัวเลือก */}
            </select>
          </div>
          <div>
            <label htmlFor="interest">ฝ่ายที่สนใจ:</label>
            <select id="about" name="about" value={formData.about} onChange={handleChange}>
              {/* ตัวเลือก */}
            </select>
          </div>
          <button className="custom-btn btn-15" type="button" onClick={handleSubmit}>สมัคร</button>
        </form>
      </div>
      <h4>โปรดกรอกข้อมูลตามความเป็นจริง</h4>
    </div>
  );
}

export default MyButton;
