import React, { useState } from 'react';
import firebase from '../../firebase/app'; // เปลี่ยน path เป็นเส้นทางที่ถูกต้องของ firebase
import 'firebase/database';




// Initialize Firebase
const firebaseConfig = {
    apiKey: "AIzaSyCe1IvDZEpMLw8iwFnJxksfiO572R6L81I",
    authDomain: "anusamo.firebaseapp.com",
    projectId: "anusamo",
    storageBucket: "anusamo.appspot.com",
    messagingSenderId: "1080095884059",
    appId: "1:1080095884059:web:c35386d184ed322b5e09be",
    measurementId: "G-WBMKFW37Q6"
};

firebase.initializeApp(firebaseConfig);
const database = firebase.database();

function MyForm() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // เพิ่มข้อมูลลงใน Realtime Database
    database.ref('users').push({
      name: name,
      age: age
    }).then(() => {
      console.log('Data added successfully!');
      // เคลียร์ฟอร์มหลังจากเพิ่มข้อมูลสำเร็จ
      setName('');
      setAge('');
    }).catch((error) => {
      console.error('Error adding data: ', error);
    });
  };

  return (
    <div>
      <h2>Add User</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        <label>Age:</label>
        <input type="text" value={age} onChange={(e) => setAge(e.target.value)} />
        <button type="submit">Add User</button>
      </form>
    </div>
  );
}

export default MyForm;
